function getTable(clustername, wordlist, tfidf){
    var str = "" // <p class='lead'>Top Words in Cluster " + clustername + "</p>";
    str += "<table padding='10px'><tr style='border-bottom:1px solid lightpink'><th>Word</th><th>TF-IDF</th></tr>";
    var i;
    for (i = 0; i < wordlist.length; i++) {
        str += "<tr><td>" + wordlist[i] + "</td><td>" + tfidf[i] + "</td></tr>";
    }
    str += "</table>";
    // alert(str);
    return str;
};

function showTweets(clustername){
	tweetlist = getTweets(clustername)
	var str = "";
    str += "<table><tr style='border-bottom:1px solid lightpink'><th>Tweets</th></tr>";
    var i;  // currently 25 tweets shown
    for (i = 0; i < tweetlist.length/2; i++) {
        str += "<tr style='border-bottom:1px solid lightpink'><td><div style='width: 600px;'>" + tweetlist[i] + "</div></td></tr>";
    }
    str += "</table>";
    str += "<br><a href=javascript:showMoreTweets(" + clustername + ")  style='color: dimgray'>" + "See more tweets" + "</a>";
    // alert(str);
    return str;
}

